/// <reference path='index.d.ts' />
import BetaLogger from 'betalogger';
/** Colored Logger */
const Colored = new BetaLogger.Colored();
Colored.emerg('Emergency');
Colored.error('Error');
Colored.warn('Warning');
Colored.notice('Notice');
Colored.info('Info');
Colored.watch('Watch');
Colored.pending('Pending');
/** Default Colorless Logger */
const Default = new BetaLogger.Default();
Default.emerg('Emergency');
Default.error('Error');
Default.warn('Warning');
Default.notice('Notice');
Default.info('Info');
Default.watch('Watch');
Default.pending('Pending');