declare module "betalogger" {
    export interface CLOptions {
        file?: string;
        timestampEnabled?: boolean|true;
        timestampColor?: string|"grey";
		process_name?: string|"NODE";
		process_color?: string|"green";
        levels?: {
            emerg?: {
                color?: string|"magenta";
                ToFile?: boolean|true;
            };
            error?: {
                color?: string|"red";
                ToFile?: boolean|true;
            };
            warn?: {
                color?: string|"yellow";
                ToFile?: boolean|true;
            };
            debug?: {
                color?: string|"blue";
                ToFile?: boolean|true;
            };
            notice?: {
                color?: string|"green";
                ToFile?: boolean|true;
            }
            info?: {
                color?: string|"cyan";
                ToFile?: boolean|true;
            };
        };
    }

    export class Colored {
        public readonly version: string;
        constructor(opts?: CLOptions);
        public emerg(message: string): void;
        public error(message: string): void;
        public warn(message: string): void;
        public debug(message: string): void;
        public notice(message: string): void;
        public info(message: string): void;
        public watch(message: string): void;
        public pending(message: string): void;
    }

    export interface DLOptions {
        file?: string;
        timestampEnabled?: boolean|true;
        levels?: {
            emerg?: {
                ToFile?: boolean|true;
            };
            error?: {
                ToFile?: boolean|true;
            };
            warn?: {
                ToFile?: boolean|true;
            };
            debug?: {
                ToFile?: boolean|true;
            };
            notice?: {
                ToFile?: boolean|true;
            }
            info?: {
                ToFile?: boolean|true;
            };
        };
    }

    export class Default {
        public readonly version: string;
        constructor(opts?: DLOptions)
        public emerg(message: string): void;
        public error(message: string): void;
        public warn(message: string): void;
        public debug(message: string): void;
        public notice(message: string): void;
        public info(message: string): void;
        public watch(message: string): void;
        public pending(message: string): void;
    }
}
